UPDATE contentstore.correspondencetemplate SET template = 'Dear {{FULLNAME}}, our Nelspruit branch address is: The Grove Shopping Centre, Corner R40 White River Road and Gorge Street, Riverside. Our telephone number is (031)752-6872 - SA Taxi.' Where identifier = 'NELSPRUIT_BRANCH_SMS'; 
UPDATE contentstore.correspondencetemplate SET template = 'SA Taxi: Dear {{FULLNAME}}, as per your request the due date for the account {{REFERENCE_NUMBER}} has been changed to the {{INSTALLMENT_DATE}} of every month. Your next installment of R {{AMOUNT}} is due on the {{DUE_DATE}}.' Where identifier = 'INSTALLMENT_CHANGE_SMS'; 
UPDATE contentstore.correspondencetemplate SET template = 'SA Taxi: Dear {{FULLNAME}}, as per your request a debit order has been activated and will take effect from the {{DUE_DATE}} for R {{AMOUNT}}.' Where identifier = 'DEBIT_ORDER_ACTIVATION_SMS';
UPDATE contentstore.correspondencetemplate SET template = 'SA Taxi: Dear {{FULLNAME}}, the debit order for account {{REFERENCE_NUMBER}} has been cancelled. Your next installment of R {{AMOUNT}} is due on the {{DUE_DATE}}. For queries, please contact us on 0861829448.' Where identifier = 'DEBIT_ORDER_CANCELLATION_SMS'; 
UPDATE contentstore.correspondencetemplate SET template = 'SA Taxi: Dear {{FULLNAME}}, the settlement amount for account {{REFERENCE_NUMBER}} is R {{AMOUNT}} until {{DUE_DATE}}. If you pay this, your contract comes to an end. Tracker and insurance will not be paid for.' Where identifier = 'SETTLEMENT_QUOTATION_SMS'; 
